stats_length(range([Min,Max]),List) :-
        stats_length('all_genome',List_Count),
        stats_range(Min,Max,List_Count,List).

% PreCond: List of Counting is sorted
stats_range(_Min,_Max,[],[]) :-
        !.

stats_range(_Min,Max,[(Num,_Count)|_Rest_Count],[]) :-
        Num > Max,
        !.

stats_range(Min,Max,[(Num,Count)|Rest_Count],[(Num,Count)|Rest_Result]) :-
        Min =< Num,
        Num =< Max,
        !,
        stats_range(Min,Max,Rest_Count,Rest_Result).

stats_range(Min,Max,[(Num,_Count)|Rest_Count],Result) :-
        Min > Num,
        !,
        stats_range(Min,Max,Rest_Count,Result).
        


